using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string htmlSourceString = "Your HTML content here with multiple hrefs";\n        string pattern = "href=[\\\"\\\'](http:\\/\\/|\\.\\/|\\/)?\\w+(\\.\\w+)*(\\/\\w+(\\.\\w+)?)*(\\/|\\?\\w*=\\w*(&\\w*=\\w*)*)?[\\\"\\\']";\n\n        MatchCollection matches = Regex.Matches(htmlSourceString, pattern);\n\n        foreach (Match m in matches)\n        {\n            if (m.Success)\n            {\n                Console.WriteLine("Found href: " + m.Value);\n            }\n        }\n    }\n}