using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Reflection;\nusing System.Web.Mvc;\n\npublic static class ControllerHelper\n{\n    public static List<string> GetControllerNames()\n    {\n        var controllerTypes = Assembly.GetExecutingAssembly().GetTypes()\n            .Where(type => typeof(Controller).IsAssignableFrom(type) && type.Name.EndsWith("Controller"));\n        \n        var controllerNames = controllerTypes.Select(controllerType => \n            controllerType.Name.Replace("Controller", "")).ToList();\n\n        return controllerNames;\n    }\n}