using Oracle.ManagedDataAccess.Client;\nusing System.Data;\n\n// Assume you have a connection already opened\nOracleConnection conn = new OracleConnection("your_connection_string");\nconn.Open();\n\n// Prepare the command object\nOracleCommand cmd = new OracleCommand("ProcessData", conn);\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Create a DataTable to hold your data\nDataTable dataTable = new DataTable();\ndataTable.Columns.Add("COL1", typeof(int));\ndataTable.Columns.Add("COL2", typeof(string));\n\n// Add rows to your DataTable as needed\ndataTable.Rows.Add(1, "Data1");\ndataTable.Rows.Add(2, "Data2");\n\n// Create an OracleParameter for the REF CURSOR\nOracleParameter refCursorParam = new OracleParameter("P_INPUT", OracleDbType.RefCursor);\nrefCursorParam.Direction = ParameterDirection.Input;\ncmd.Parameters.Add(refCursorParam);\n\n// Use OracleDataAdapter to fill a DataSet from the DataTable\nOracleDataAdapter da = new OracleDataAdapter();\nda.InsertCommand = cmd;\n\n// Create an OracleDataReader from DataTable and set it as input of the REF CURSOR parameter\nusing (OracleDataReader reader = dataTable.CreateDataReader())\n{\n   refCursorParam.Value = reader;\n   cmd.ExecuteNonQuery();\n}\n\nconn.Close();