using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        XDocument saleResults = XDocument.Load(@"temp/salesData.xml");\n\n        var sortedItems = \n            (from item in saleResults.Descendants("item")\n             let dateTimeValue = DateTime.Parse(item.Attribute("sTime").Value)\n             orderby dateTimeValue descending\n             select item).ToArray();\n\n        foreach (var item in sortedItems)\n        {\n            Console.WriteLine($"Name: {item.Attribute("name")?.Value}, SKU: {item.Attribute("sku")?.Value}, Time: {item.Attribute("sTime")?.Value}");\n        }\n    }\n}