// Example of adding form fields dynamically in ASP.NET Web Forms\n   protected void Page_Load(object sender, EventArgs e)\n   {\n       TextBox txtFirstName = new TextBox();\n       RequiredFieldValidator rfvFirstName = new RequiredFieldValidator { ControlToValidate = txtFirstName.ID, Text="First name is required." };\n       this.formControls.Controls.Add(txtFirstName);\n       this.formControls.Controls.Add(rfvFirstName);\n\n       TextBox txtLastName = new TextBox();\n       RequiredFieldValidator rfvLastName = new RequiredFieldValidator { ControlToValidate = txtLastName.ID, Text = "Last name is required." };\n       this.formControls.Controls.Add(txtLastName);\n       this.formControls.Controls.Add(rfvLastName);\n   }