public class FontManager\n{\n    private readonly Control _control;\n    private float _fontSize;\n\n    public FontManager(Control control)\n    {\n        _control = control;\n        _fontSize = control.Font.Size;\n    }\n\n    public void SetFontSize(float newSize)\n    {\n        // Dispose the old font to prevent memory leaks\n        if (_control.Font != null)\n        {\n            _control.Font.Dispose();\n        }\n        \n        // Create a new font with the specified size and apply it\n        _control.Font = new Font(_control.Font.FontFamily, newSize);\n        _fontSize = newSize;\n    }\n\n    public float GetFontSize()\n    {\n        return _fontSize;\n    }\n}