// Code-behind (C#)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Bind an empty data source to the GridView on initial load\n        BindEmptyDataSource();\n    }\n}\n\nprivate void BindEmptyDataSource()\n{\n    // Create a DataTable with no rows but with column definitions\n    DataTable dt = new DataTable();\n    dt.Columns.Add("ID", typeof(int));\n    dt.Columns.Add("Name", typeof(string));\n\n    // Bind this empty DataTable to the GridView\n    GridView1.DataSource = dt;\n    GridView1.DataBind();\n\n    // Manually create the FooterRow even with an empty ds\n    if (GridView1.Rows.Count == 0)\n    {\n        GridView1.Rows.Add(GridView1.FooterRow);\n    }\n}\n\nprotected void GridView1_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "Add")\n    {\n        // Logic to handle adding a new record\n        AddNewRecord();\n    }\n}\n\nprivate void AddNewRecord()\n{\n    // Code to add a new record to your data source\n    // For example:\n    /*\n    string id = ((TextBox)GridView1.FooterRow.FindControl("txtID")).Text;\n    string name = ((TextBox)GridView1.FooterRow.FindControl("txtName")).Text;\n\n    // Here you would insert into your database\n\n    BindGridView(); // Rebind the GridView to show new data\n     */\n}