using System;\nusing System.Xml;\n\npublic class SharePointListServiceExample\n{\n    public static void Main()\n    {\n        // Create a reference to the Lists web service.\n        var Lists = new Web_Reference.Lists();\n        \n        // Set credentials (use default network credentials if applicable).\n        Lists.Credentials = System.Net.CredentialCache.DefaultNetworkCredentials;\n        \n        // Define the list name or GUID, and view ID or GUID.\n        string listName = "YourListName"; \n        string batchCommand =\n            "<Batch OnError='Continue'>" +\n                "<Method ID='1' Cmd='New'>" +\n                    "<FieldRef Name='Title'/><Value Type='Text'>Item Title</Value>" +\n                    "<FieldRef Name='AnotherTextField'/><Value Type='Text'>Some other text</Value>" +\n                    "<FieldRef Name='ChoiceFieldName'/><Value Type='Choice'>Option 1</Value>" +\n                "</Method>" +\n            "</Batch>";\n        \n        // Call the UpdateListItems method.\n        XmlDocument xmlDoc = new XmlDocument();\n        XmlNode nodeResults = Lists.UpdateListItems(listName, xmlDoc.LoadXml(batchCommand));\n        \n        // Display the results of the batch operation.\n        Console.WriteLine(nodeResults.OuterXml);\n    }\n}