using System.Security.Principal;\nusing System.Web;\n\npublic class ADAuthorizationHelper\n{\n    public static bool IsUserInRole(string role)\n    {\n        if (HttpContext.Current != null && HttpContext.Current.User.Identity.IsAuthenticated)\n        {\n            return HttpContext.Current.User.IsInRole(role);\n        }\n        return false;\n    }\n\n    public static WindowsIdentity GetCurrentUser()\n    {\n        return HttpContext.Current == null ? null : HttpContext.Current.Request.LogonUserIdentity;\n    }\n}