public class Customer\n{\n    public virtual int Id { get; set; }\n    public virtual string Name { get; set; }\n    public virtual decimal Balance { get; set; }\n\n    // You can add more properties similarly as needed\n}\n\npublic class Entity\n{\n    public virtual int EntityId { get; set; }\n    public virtual IList<EntityVarchar> VarcharAttributes { get; set; }\n    public virtual IList<EntityFloat> FloatAttributes { get; set; }\n\n    // Initialize lists in a constructor or elsewhere\n    public Entity()\n    {\n        VarcharAttributes = new List<EntityVarchar>();\n        FloatAttributes = new List<EntityFloat>();\n    }\n}\n\npublic class EntityVarchar\n{\n    public virtual int EntityId { get; set; }\n    public virtual string AttributeName { get; set; }\n    public virtual string Value { get; set; }\n}\n\npublic class EntityFloat\n{\n    public virtual int EntityId { get; set; }\n    public virtual string AttributeName { get; set; }\n    public virtual decimal Value { get; set; }\n}