public class MultiLineTreeView : TreeView {\n    public MultiLineTreeView() {\n        DrawMode = TreeViewDrawMode.OwnerDrawText;\n    }\n\n    protected override void OnDrawNode(DrawTreeNodeEventArgs e) {\n        base.OnDrawNode(e);\n\n        if ((e.State & TreeNodeStates.Selected) != 0)\n            e.Graphics.FillRectangle(SystemBrushes.Highlight, e.Bounds);\n        else\n            e.Graphics.FillRectangle(Brushes.White, e.Bounds);\n\n        using (StringFormat sf = new StringFormat { LineAlignment = StringAlignment.Near }) {\n            string[] lines = e.Node.Text.Split('\n');\n            int y = e.Bounds.Y;\n            foreach (string line in lines) {\n                Rectangle textRect = new Rectangle(e.Bounds.X, y, e.Bounds.Width, e.Bounds.Height);\n                e.Graphics.DrawString(line, this.Font, Brushes.Black, textRect, sf);\n                y += 20; // Adjust this value based on your needs\n            }\n        }\n\n        if (e.Node.IsExpanded && e.Node.Nodes.Count > 0)\n            e.Graphics.DrawImage(SystemIcons.Application.ToBitmap(), e.Bounds.X + 16, e.Bounds.Y + 4);\n\n        if ((e.State & TreeNodeStates.Selected) != 0)\n            e.DrawDefault = false;\n    }\n}