using System.IO;\nusing System.Web.Mvc;\n\nnamespace MyApp.Controllers\n{\n    public class AccountController : Controller\n    {\n        // Define the route for this action method using attribute routing\n        [HttpGet]\n        [Route("accounts/{id}")]\n        public ActionResult GetAccount(string id)\n        {\n            // this is just an example, it would normally include caching and \n            // a template system\n            string html = File.ReadAllText(Server.MapPath("~/accounts/accounts.htm"));\n            html = html.Replace("id", id);\n            return Content(html, "text/html");\n        }\n    }\n}