using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\nusing iText.Kernel.Pdf;\nusing iText.Forms;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string pdfPath = "path/to/your/form.pdf";\n        string outputPath = "output.csv";\n\n        using (PdfReader pdfReader = new PdfReader(pdfPath))\n        using (PdfDocument pdfDoc = new PdfDocument(pdfReader))\n        {\n            var formFields = PdfAcroForm.GetAcroForm(pdfDoc, true).GetFormFields();\n\n            using (var writer = new StreamWriter(outputPath))\n            {\n                writer.WriteLine(string.Join(",", formFields.Keys)); // Write headers\n                foreach (var field in formFields)\n                {\n                    writer.WriteLine($"{field.Key},{ExtractValue(field.Value)}");\n                }\n            }\n        }\n\n        Console.WriteLine("Data extracted to CSV successfully.");\n    }\n\n    static string ExtractValue(PdfFormField field)\n    {\n        return string.Join(",", field.GetValues().Select(v => v.GetValueAsString()));\n    }\n}