using System;\nusing System.Reflection;\n\npublic class AssemblyChecker {\n    public static void CheckStrongName(Assembly assembly) {\n        Console.WriteLine("Is the assembly strongly named? " + (assembly.GetName().GetPublicKeyToken() != null && assembly.GetName().GetPublicKeyToken().Length > 0));\n    }\n}\n\nclass Program {\n    static void Main(string[] args) {\n        // Example: check current assembly\n        AssemblyChecker.CheckStrongName(Assembly.GetExecutingAssembly());\n    }\n}