using System;\n\nclass Program {\n    static void Main() {\n        double n = 5.0;\n        \n        // Using multiplication operator\n        double result1 = n * n * n; // Equivalent to Math.Pow(n, 3)\n        \n        // Using Math.Pow method\n        double result2 = Math.Pow(n, 3);\n        \n        Console.WriteLine($"Multiplication: {result1}");\n        Console.WriteLine($"Math.Pow: {result2}");\n    }\n}