using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument doc = new XmlDocument();\n        doc.Load("Foo.exe.config");\n\n        // Select the baseAddress node using XPath\n        XmlNode baseAddressNode = doc.SelectSingleNode(\n            "/configuration/system.serviceModel/services/service/host/baseAddresses/add[@baseAddress]");\n\n        if (baseAddressNode != null)\n        {\n            // Get the current value\n            string currentValue = baseAddressNode.Attributes["baseAddress"].Value;\n            Console.WriteLine("Current Base Address: " + currentValue);\n\n            // Update the value\n            baseAddressNode.Attributes["baseAddress"].Value = "http://newaddress:9090/Foo";\n            \n            // Save the changes back to the file\n            doc.Save("Foo.exe.config");\n        }\n    }\n}