public class MyDbContext : DbContext\n{\n    public virtual DbSet<MyTable> MyTables { get; set; }\n\n    [DbFunction("MySchema", "GetItems")]\n    public IQueryable<MyTable> GetItems(string stuff)\n    {\n        var stuffParameter = new ObjectParameter("Stuff", typeof(string)) { Value = stuff };\n        return ((IObjectContextAdapter)this).ObjectContext.CreateQuery<MyTable>("[MySchema].[GetItems](@Stuff)", stuffParameter);\n    }\n}