public class ProjectAuthorizationHandler : AuthorizationHandler<ProjectAuthorizationRequirement>\n{\n    protected override Task HandleRequirementAsync(AuthorizationHandlerContext context, \n                                                   ProjectAuthorizationRequirement requirement)\n    {\n        if (context.User.HasClaim("ProjectRole", requirement.RoleName))\n        {\n            context.Succeed(requirement);\n        }\n\n        return Task.CompletedTask;\n    }\n}\n\npublic class ProjectAuthorizationRequirement : IAuthorizationRequirement\n{\n    public string RoleName { get; set; }\n\n    public ProjectAuthorizationRequirement(string roleName)\n    {\n        RoleName = roleName;\n    }\n}