protected void Page_Load(object sender, EventArgs e)\n{\n    // Check if the session is new or has timed out\n    if (Session.IsNewSession == true && Request.Cookies["ASP.NET_SessionId"] != null)\n    {\n        // This means the session has expired and a new one will be created.\n        string sessionCookie = Request.Cookies["ASP.NET_SessionId"].Value;\n        if (HttpContext.Current.Session != null)\n        {\n            // Session is not really expired, so we're good to renew it\n            RenewSession();\n        }\n    }\n\n    // Check for user activity and renew session as needed\n    if (!IsPostBack)\n    {\n        return; // We only want to refresh on postbacks (e.g., button clicks, form submissions)\n    }\n    \n    RenewSession();\n}\n\nprivate void RenewSession()\n{\n    // Logic to handle session renewal\n    Session["LastActivity"] = DateTime.Now;\n}