using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "path_to_your_file.txt";\n\n        if (!File.Exists(filePath))\n        {\n            Console.WriteLine("File not found.");\n            return;\n        }\n\n        string[] lines = File.ReadAllLines(filePath);\n\n        foreach (string line in lines)\n        {\n            // Split the line by tab character\n            string[] parts = line.Split('\t');\n\n            if (parts.Length < 5) // Ensure there are enough parts to work with\n            {\n                continue;\n            }\n\n            // Convert the second part to integer\n            if (int.TryParse(parts[1], out int secondInteger))\n            {\n                Console.WriteLine($"Second Integer: {secondInteger}");\n\n                // Find the path string that starts with "item\" and ends with ".ddj"\n                foreach (string part in parts)\n                {\n                    if (part.StartsWith("item\\") && part.EndsWith(".ddj"))\n                    {\n                        Console.WriteLine($"Path: {part}");\n                        break;\n                    }\n                }\n            }\n        }\n    }\n}