using System;\nusing System.ServiceModel;\n\npublic class Program\n{\n    static void Main(string[] args)\n    {\n        // Create an instance of your service implementation\n        var host = new ServiceHost(typeof(MyService));\n\n        // Define a binding using TCP\n        NetTcpBinding binding = new NetTcpBinding();\n\n        // Set up the endpoint with the binding and contract type\n        host.AddServiceEndpoint(typeof(IMyContract), binding, "net.tcp://localhost:8000/myservice");\n\n        try\n        {\n            host.Open();\n            Console.WriteLine("Service is ready.");\n            Console.ReadLine(); // Keep running\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}\n\n[ServiceContract]\npublic interface IMyContract\n{\n    [OperationContract(IsOneWay = true)]\n    void SendMessage(string message);\n}\n\npublic class MyService : IMyContract\n{\n    public void SendMessage(string message)\n    {\n        // Handle the received message\n        Console.WriteLine($"Received: {message}");\n    }\n}