using System;\n   using System.Collections.Generic;\n   using SharpDbf;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           // Open DBF file\n           using (var dbase = DbaseFile.OpenReadOnly("path_to_your_file.dbf"))\n           {\n               // Create a list to hold all records\n               List<object[]> records = new List<object[]>();\n\n               // Loop through the records and print them\n               foreach (DbfRow row in dbase.ReadRows())\n               {\n                   object[] itemArray = row.ItemArray;\n                   records.Add(itemArray);\n\n                   // Print each field in this record\n                   foreach (var item in itemArray)\n                   {\n                       Console.Write(item.ToString() + ", ");\n                   }\n                   Console.WriteLine();\n               }\n\n               // Optionally, process records and insert into your database\n           }\n       }\n   }