using System.Runtime.InteropServices;\n\nclass Program\n{\n    // Import the DLL and define the function signature\n    [DllImport("YourDllName.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern int Add(int a, int b);\n\n    static void Main()\n    {\n        int result = Add(5, 3);\n        Console.WriteLine("Result: " + result); // Should print Result: 8\n    }\n}