using System;\nusing System.Web.Services;\n\n[WebService(Namespace = "http://tempuri.org/")]\npublic class MyWebService : WebService\n{\n    [WebMethod]\n    public string HelloWorld()\n    {\n        // Get the client's IP address and port number\n        string clientIP = HttpContext.Current.Request.UserHostAddress;\n        string clientPort = HttpContext.Current.Request.ServerVariables["REMOTE_PORT"];\n\n        // Log the information (This is a simple example, in production you might want to log this information using a logging framework)\n        Console.WriteLine($"Client IP: {clientIP}, Client Port: {clientPort}");\n\n        return "Hello World";\n    }\n}