using System.Net;\n\n// Create a new WebProxy instance with your proxy server details.\nIWebProxy myProxy = new WebProxy("http://yourproxy:port", true);\n\n// Set the default Credentials to use for authenticating with the proxy.\nmyProxy.Credentials = CredentialCache.DefaultCredentials;\n\n// Assign the proxy to the ServicePointManager\nGlobalProxySelection.Select = myProxy;\n\n// Optionally, if you need to accept all certificates (e.g., self-signed), configure this.\nServicePointManager.ServerCertificateValidationCallback += \n    delegate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n{\n    return true; // This is not recommended for production\n};