using System.Collections.Generic;\nusing System.Linq;\nusing System.Web.Mvc;\nusing YourNamespace.Properties; // Adjust namespace according to your project\n\npublic class YourController : Controller\n{\n    public ActionResult YourAction()\n    {\n        var model = new YourViewModel();\n        \n        // Accessing resource values and creating a list of SelectListItem\n        model.YourDropDownItems = \n            Enum.GetValues(typeof(MyResources))  // Adjust this as per how you store the items in the resource file\n                .Cast<MyResources>()             // Cast to your enum or however you manage the keys/values in resources\n                .Select(e => new SelectListItem { Text = Resources.ResourceManager.GetString(e.ToString()), Value = e.ToString() })\n                .ToList();\n        \n        return View(model);\n    }\n}