using System;\nusing System.Linq.Expressions;\n\npublic static class MappingExtension\n{\n    public static void AddMapping<TControl, TObject>(this TControl control, TObject obj, Expression<Func<TObject, object>> propertyExpression)\n        where TControl : Control // Assuming myControl is a subclass of Control\n    {\n        if (propertyExpression.Body is not MemberExpression memberExpression)\n        {\n            throw new ArgumentException("Property expression must be a member access.", nameof(propertyExpression));\n        }\n\n        string propertyName = memberExpression.Member.Name;\n\n        // Now you can use the propertyName for your mapping logic\n        Console.WriteLine($"Mapping control to property: {propertyName}");\n    }\n}