using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class AsymmetricEncryptionExample\n{\n    public static void Main()\n    {\n        string original = "ClientRemoteAccessPassword";\n\n        // Generate RSA keys\n        using (var rsa = new RSACryptoServiceProvider())\n        {\n            // Encrypt the data to an array of bytes.\n            byte[] encryptedData = rsa.Encrypt(Encoding.UTF8.GetBytes(original), true);\n            \n            Console.WriteLine("Encrypted Data: {0}", Convert.ToBase64String(encryptedData));\n            \n            // Decrypt the byte array and place in a string.\n            byte[] decryptedData = rsa.Decrypt(encryptedData, true);\n            string decryptedOriginal = Encoding.UTF8.GetString(decryptedData);\n            \n            Console.WriteLine("Decrypted Data: {0}", decryptedOriginal);\n        }\n    }\n}