protected void Form_addRoom_ItemInserting(object sender, FormViewInsertEventArgs e)\n{\n    // Assume enteredMaxPeople is retrieved from the form data\n    int enteredMaxPeople = Convert.ToInt32((FormView1.FindControl("maxPeopleTextBox") as TextBox).Text);\n\n    // Perform your database query to get queryMaxPeople\n    using (SqlConnection conn = new SqlConnection("<YourConnectionString>"))\n    {\n        conn.Open();\n        using (SqlCommand cmd = new SqlCommand("SELECT MaxPeople FROM Rooms WHERE RoomID = @RoomID", conn))\n        {\n            // Add parameters if necessary, e.g., for filtering specific room data\n            //cmd.Parameters.AddWithValue("@RoomID", someRoomId);\n\n            int queryMaxPeople = Convert.ToInt32(cmd.ExecuteScalar());\n\n            if (enteredMaxPeople > queryMaxPeople)\n            {\n                // Cancel the insertion\n                e.Cancel = true;\n                \n                // Optionally, update a label to notify the user\n                Label statusLabel = FormView1.FindControl("statusLabel") as Label;\n                if (statusLabel != null)\n                {\n                    statusLabel.Text = "Value entered not valid";\n                    statusLabel.ForeColor = System.Drawing.Color.Red; // Optional: Set color for emphasis\n                }\n            }\n        }\n    }\n}