protected override void OnKeyDown(KeyEventArgs e)\n{\n    // Allow all non-printable keys (like Backspace, Tab, etc.)\n    if (!char.IsControl(e.KeyChar))\n    {\n        // Check for valid characters using regex\n        bool isValidChar = Regex.Match(e.KeyChar.ToString(), this._ValidCharExpression).Success;\n        if (!isValidChar)\n        {\n            e.Handled = true; // Ignore the character if it's not valid\n        }\n    }\n\n    // Allow Ctrl combinations like Ctrl+C, Ctrl+V\n    if (e.Control && (e.KeyCode == Keys.C || e.KeyCode == Keys.V))\n    {\n        base.OnKeyDown(e); // Handle as normal (allow these key combinations)\n        return;\n    }\n\n    base.OnKeyDown(e);\n}