using System;\nusing Oracle.DataAccess.Client;\n\nnamespace OracleConnectionExample\n{\n    class Program\n    {\n        static void Main()\n        {\n            string connectionString = "User Id=yourId;Password=yourPassword;Data Source=(DESCRIPTION=(ADDRESS_LIST=(ADDRESS=(PROTOCOL=tcp)(HOST=yourHost)(PORT=yourPort)))(CONNECT_DATA=(SERVICE_NAME=yourServiceName)));";\n            \n            using (OracleConnection conn = new OracleConnection(connectionString))\n            {\n                try\n                {\n                    conn.Open();\n                    Console.WriteLine("Connected to Oracle!");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine(ex.Message);\n                }\n            }\n        }\n    }\n}