using RazorEngine;\nusing RazorEngine.Templating;\nusing System;\n\npublic class Product\n{\n    public string Name { get; set; }\n    public string Description { get; set; }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Define your model (object to render)\n        var product = new Product \n        { \n            Name = "Sample Product", \n            Description = "This is a great product for everything!" \n        };\n\n        // Define the template string (you can also load this from a file)\n        string template = @"\n<div>\n    <h3>@Model.Name</h3>\n    <p>@Model.Description</p>\n</div>";\n\n        // Compile and run the template\n        string htmlOutput = Engine.Razor.RunCompile(template, "productKey", typeof(Product), product);\n\n        // Output the result\n        Console.WriteLine(htmlOutput);\n    }\n}