var query = \n    from t in db.Import_Tables\n    join ch in db.Import_CheckoutHistory on t.import_CheckoutHistoryID equals ch.import_CheckoutHistoryID into checkoutHistories\n    from chs in checkoutHistories.DefaultIfEmpty()\n    // Assuming a default if empty is needed for System_User as well, adjust based on actual requirements\n    join su in db.System_Users on chs.system_UserID equals su.system_UserID into systemUsers\n    from sus in systemUsers.DefaultIfEmpty()\n    select new {\n        CheckedOutUser = (!t.IsCheckedOut) ? "--" : sus.FirstName + " " + sus.LastName,\n        CheckedOutUserID = (!t.IsCheckedOut) ? 0 : chs.system_UserID\n    };