using System;\nusing Microsoft.CSharp;\n\nclass Program\n{\n    static void Main()\n    {\n        string className = "myClass1";\n        \n        if (IsIdentifier(className))\n        {\n            Console.WriteLine($"{className} is a valid C# identifier.");\n        }\n        else\n        {\n            Console.WriteLine($"{className} is NOT a valid C# identifier.");\n        }\n    }\n\n    public static bool IsIdentifier(string name)\n    {\n        var provider =(new CSharpCodeProvider());\n        return provider.IsValidIdentifier(name);\n    }\n}