[System.Web.Services.WebMethod]\npublic static List<YourDataType> GetDataForGridView(string gridViewId)\n{\n    // Implement logic to fetch data for the GridView based on gridViewId\n    var dataTable = new DataTable();\n    // Fill dataTable with data\n    return ToList(dataTable);\n}\n\nprivate static List<YourDataType> ToList(DataTable dt)\n{\n    List<YourDataType> lst = new List<YourDataType>();\n    foreach (DataRow row in dt.Rows)\n    {\n        lst.Add(new YourDataType()\n        {\n            Property1 = Convert.ToString(row["Column1"]),\n            // Map other properties...\n        });\n    }\n    return lst;\n}