using System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic byte[] CreatePdfInMemory()\n{\n    // Create a new memory stream to hold the PDF data\n    using (var ms = new MemoryStream())\n    {\n        // Initialize a new document\n        Document document = new Document();\n        \n        // Create PdfWriter instance linking document and stream\n        PdfWriter.GetInstance(document, ms);\n        \n        // Open the document for writing\n        document.Open();\n        \n        // Add content to the document\n        document.Add(new Paragraph("Hello, this is a test PDF!"));\n        \n        // Close the document\n        document.Close();\n\n        // Convert the memory stream contents to a byte array\n        return ms.ToArray();\n    }\n}