using System;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] myArray = { "apple", "banana", "cherry", "date", /* ... */ };\n        RandomizeArray(myArray);\n        \n        // Print the randomized array\n        foreach (string item in myArray)\n        {\n            Console.WriteLine(item);\n        }\n    }\n\n    static void RandomizeArray(string[] array)\n    {\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            int n = array.Length;\n            while (n > 1)\n            {\n                byte[] data = new byte[sizeof(uint)];\n                do\n                {\n                    rng.GetBytes(data);\n                } while (!BitConverter.IsUInt32LittleEndian && BitConverter.ToUInt32(data, 0) >= uint.MaxValue / n * n);\n\n                int k = BitConverter.ToUInt32(data, 0) % n;\n                n--;\n\n                string temp = array[n];\n                array[n] = array[k];\n                array[k] = temp;\n            }\n        }\n    }\n}