using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> listA = new List<string>();\n        List<string> listB = new List<string>();\n        List<string> listC = new List<string>();\n\n        using (SqlConnection connection = new SqlConnection("YourConnectionStringHere"))\n        {\n            SqlCommand command = new SqlCommand(\n                "SELECT A.colA, B.colB, C.colC FROM A INNER JOIN B ON A.id = B.id INNER JOIN C ON A.id = C.id", \n                connection);\n\n            connection.Open();\n\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    listA.Add(reader["colA"].ToString());\n                    listB.Add(reader["colB"].ToString());\n                    listC.Add(reader["colC"].ToString());\n                }\n            }\n        }\n\n        // Now listA, listB, and listC contain your data\n    }\n}