using System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        Process updater = new Process();\n        updater.StartInfo.FileName = "Updater.exe";\n        updater.StartInfo.Arguments = "arg1 arg2"; // Ensure these arguments are correct\n        updater.StartInfo.UseShellExecute = false; // This is important if you need to redirect input/output or handle credentials\n        updater.Start(); // Start the updater process\n\n        // Optionally, wait for it to start before exiting\n        updater.WaitForInputIdle();\n\n        // Exit the parent process\n    }\n}