using Microsoft.Deployment.WindowsInstaller;\nusing Microsoft.Win32;\n\n[CustomAction]\npublic static ActionResult RemoveRegistryEntries(Session session)\n{\n    string keyPath = @"SOFTWARE\Microsoft\VisualStudio\8.0\PreloadAddinStateManaged\MyAddinName.Connect";\n    using (RegistryKey regKey = Registry.CurrentUser.OpenSubKey(keyPath, true))\n    {\n        if (regKey != null)\n        {\n            foreach (string valueName in regKey.GetValueNames())\n            {\n                if (valueName.StartsWith("YourPrefix"))\n                {\n                    regKey.DeleteValue(valueName);\n                }\n            }\n        }\n    }\n\n    return ActionResult.Success;\n}