using System;\n\nclass Program\n{\n    static void Main()\n    {\n        int number = 17463;\n        int sumOfDigits = SumDigits(number);\n        Console.WriteLine("Sum of digits: " + sumOfDigits);\n    }\n\n    static int SumDigits(int number)\n    {\n        int sum = 0;\n        foreach (char digit in number.ToString())\n        {\n            sum += digit - '0'; // Convert char to int\n        }\n        return sum;\n    }\n}