using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Text;\nusing System.Xml.Linq;\n\npublic class LastFmExample\n{\n    public static async void FetchAndParseXml()\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                // Replace with actual API endpoint and parameters\n                string url = "http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=cher&api_key=YourAPIKey&format=xml";\n                HttpResponseMessage response = await client.GetAsync(url);\n                \n                if (response.IsSuccessStatusCode)\n                {\n                    // Read the response content\n                    string xmlString = await response.Content.ReadAsStringAsync();\n                    \n                    // Log or print the response for inspection\n                    Console.WriteLine("Response:");\n                    Console.WriteLine(xmlString);\n\n                    // Attempt to parse as XDocument\n                    using (TextReader reader = new StringReader(xmlString))\n                    {\n                        XDocument xdoc = XDocument.Load(reader);\n                        \n                        // Now you can use LINQ to XML with xdoc\n                        Console.WriteLine("XML Parsed Successfully!");\n                    }\n                }\n                else\n                {\n                    Console.WriteLine($"Error: {response.StatusCode}");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Exception: {ex.Message}");\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        FetchAndParseXml();\n    }\n}