using OpenQA.Selenium;\nusing System.Collections.Generic;\n\npublic class LoginPage\n{\n    private readonly IWebDriver _driver;\n\n    public LoginPage(IWebDriver driver)\n    {\n        _driver = driver;\n    }\n\n    // Define properties for each element\n    private IWebElement UsernameInput => _driver.FindElement(By.Id("username"));\n    private IWebElement PasswordInput => _driver.FindElement(By.Id("password"));\n    private IWebElement LoginButton => _driver.FindElement(By.Id("login-button"));\n\n    public DashboardPage Login(string username, string password)\n    {\n        UsernameInput.SendKeys(username);\n        PasswordInput.SendKeys(password);\n        LoginButton.Click();\n        return new DashboardPage(_driver); // or however your pages link up\n    }\n}\n\npublic class DashboardPage\n{\n    private readonly IWebDriver _driver;\n\n    public DashboardPage(IWebDriver driver)\n    {\n        _driver = driver;\n    }\n\n    // Define properties and methods for the dashboard page elements\n}