// Example of TenantID filtering in a WCF service method\npublic Customer GetCustomer(int customerId, int tenantId)\n{\n    using (var context = new MyDbContext())\n    {\n        var customer = context.Customers\n            .Where(c => c.CustomerId == customerId && c.TenantId == tenantId)\n            .FirstOrDefault();\n        \n        // Return the customer or throw an exception if not found\n        return customer ?? throw new Exception("Customer not found");\n    }\n}