using System;\n\npublic class ArraySubsetChecker\n{\n    public static bool IsSubset(byte[] smallArray, byte[] largeArray)\n    {\n        if (smallArray == null || largeArray == null || smallArray.Length > largeArray.Length)\n            return false;\n\n        for (int i = 0; i <= largeArray.Length - smallArray.Length; i++)\n        {\n            int j;\n            for (j = 0; j < smallArray.Length && largeArray[i + j] == smallArray[j]; j++) ;\n\n            if (j == smallArray.Length)\n                return true;\n        }\n\n        return false;\n    }\n    \n    public static void Main()\n    {\n        byte[] smallArray = { 1, 2, 3 };\n        byte[] largeArray = { 5, 4, 1, 2, 3, 6 };\n\n        bool result = IsSubset(smallArray, largeArray);\n        Console.WriteLine("Is subset: " + result); // Output: Is subset: True\n    }\n}