// Assuming you have an Employee class in your dbml file mapped to the Employees table\nusing (var db = new YourDataContext())\n{\n    // Constructing a LINQ query to select employees and format their name display\n    var employeeList = from emp in db.Employees\n                       select new { \n                           FullName = $"{emp.LastName}, {emp.FirstName}", \n                           emp.ID \n                       };\n\n    // Binding the data to the ListBox\n    listBox.DataSource = employeeList;\n    listBox.DataTextField = "FullName"; // This will show as each item in the ListBox\n    listBox.DataValueField = "ID";     // This is bound to the value for each item\n    listBox.DataBind();\n}