using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Order\n{\n    public int CustomerId { get; set; }\n    public int ProductId { get; set; }\n    public int ProductCount { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<Order> orders = new List<Order>\n        {\n            new Order { CustomerId = 1, ProductId = 101, ProductCount = 2 },\n            new Order { CustomerId = 1, ProductId = 101, ProductCount = 3 },\n            new Order { CustomerId = 1, ProductId = 102, ProductCount = 5 },\n            new Order { CustomerId = 2, ProductId = 101, ProductCount = 4 },\n            // Add more orders as needed\n        };\n\n        var groupedOrders = orders\n            .GroupBy(order => new { order.CustomerId, order.ProductId })\n            .Select(group => new \n            {\n                group.Key.CustomerId,\n                group.Key.ProductId,\n                TotalProductCount = group.Sum(item => item.ProductCount)\n            });\n\n        foreach (var item in groupedOrders)\n        {\n            Console.WriteLine($"CustomerId: {item.CustomerId}, ProductId: {item.ProductId}, TotalProductCount: {item.TotalProductCount}");\n        }\n    }\n}