using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [ComImport]\n    // Assuming this is your COM interface\n    [Guid("YourCOMObjectGUID")]\n    public interface IMyComObject\n    {\n        void PrintDrawing();\n    }\n\n    static void Main(string[] args)\n    {\n        foreach (var record in records) // Assume 'records' is your collection of records\n        {\n            try\n            {\n                // Code for printing SSRS report, assuming this works fine\n\n                // Create and use the COM object to print drawing\n                IMyComObject comObject = null;\n                try\n                {\n                    comObject = (IMyComObject)Activator.CreateInstance(Type.GetTypeFromProgID("Your.COM.Object.ProgID"));\n                    comObject.PrintDrawing();\n                }\n                catch (COMException comEx)\n                {\n                    // Handle COM-specific exceptions\n                    Console.WriteLine($"COM Exception occurred: {comEx.Message}");\n                }\n                catch (Exception ex)\n                {\n                    // Handle other exceptions\n                    Console.WriteLine($"General exception occurred: {ex.Message}");\n                }\n                finally\n                {\n                    // Release the COM object\n                    if (comObject != null)\n                    {\n                        Marshal.ReleaseComObject(comObject);\n                        comObject = null;\n                    }\n                }\n\n            }\n            catch (Exception e)\n            {\n                // Outer try-catch to ensure no unhandled exception takes down the program\n                Console.WriteLine($"An error occurred while processing record: {e.Message}");\n            }\n        }\n    }\n}