ObjectFactory.Initialize(registry =>\n{\n    registry.Scan(assemblyScanner =>\n    {\n        // Include the calling assembly in scanning.\n        assemblyScanner.AssembliesFromApplicationBaseDirectory(); // Better practice for local assemblies\n\n        // Include specific DLLs from a directory named "extensions".\n        string path = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "extensions");\n        assemblyScanner.AssembliesFromPath(path, file => file.Contains("extension"));\n\n        // Look for all classes that derive from StructureMap's Registry class.\n        assemblyScanner.LookForRegistries();\n    });\n});