using System;\nusing System.Drawing;\n\npublic class BitmapAverageColor\n{\n    public static void Main(string[] args)\n    {\n        // Load your image\n        using (Bitmap bitmap = new Bitmap("path_to_your_image.jpg"))\n        {\n            Color averageColor = GetAverageColor(bitmap);\n            Console.WriteLine($"Average Color: R={averageColor.R}, G={averageColor.G}, B={averageColor.B}");\n        }\n    }\n\n    public static Color GetAverageColor(Bitmap bitmap)\n    {\n        int totalRed = 0;\n        int totalGreen = 0;\n        int totalBlue = 0;\n        int totalPixels = bitmap.Width * bitmap.Height;\n\n        for (int y = 0; y < bitmap.Height; y++)\n        {\n            for (int x = 0; x < bitmap.Width; x++)\n            {\n                Color pixelColor = bitmap.GetPixel(x, y);\n                totalRed += pixelColor.R;\n                totalGreen += pixelColor.G;\n                totalBlue += pixelColor.B;\n            }\n        }\n\n        // Calculate average values\n        int avgR = totalRed / totalPixels;\n        int avgG = totalGreen / totalPixels;\n        int avgB = totalBlue / totalPixels;\n\n        return Color.FromArgb(avgR, avgG, avgB);\n    }\n}